package training;

public class Countodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1,count=0;
		while(n<=10)
		{
			System.out.print(n +" ");
			n=n+2;
			count=count+1;
		}
		System.out.println(" ");
		System.out.println("Count is:" +count);
		
		int y=1;
		while(y<=40)
		{
			System.out.print(y +" ");
			y=y*2;
		}
			

	}

}
