package training;

public class Even10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1;
		while(n<=10)
		{
			n++;
			System.out.print(n +" ");
			n++;
		}
		System.out.print("\n");
		int x=0;
		while(x<=10)
		{
			System.out.print(x + " ");
			x=x+2;
			
		}

	}

}
