package training;

public class Multi3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1;
		while(n<=10)
		{
			n=n+2;
			System.out.print(n +" ");
			n++;
		}
		System.out.print("\n");
		int x=1;
		while(x<=10)
		{
			x--;
			System.out.print(x +" ");
			x=x+2;
		}
		System.out.print("\n");
		int y=0;
		while(y<=10)
		{
			y++;
			System.out.print(y +" ");
			y=y+2;
		}
		System.out.print("\n");
		int z=1;
		while(z<=10)
		{
			z++;
			z++;
			System.out.print(z +" ");
			z=z+2;
		}
		
		

	}

}
