package training;

public class Multiple {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1;
		while(n<=10)
		{
			System.out.print(n +" ");
			n=n*2;
		}
		System.out.println(" ");
		int y=1;
		while(y<=10)
		{
			y=y*2;
			System.out.print(y +" ");
			
		}
		System.out.println(" ");
		int z=1;
		while(z<=10)
		{
			z++;
			System.out.print(z +" ");
			z=z*2;
		}

	}

}
